    private void botonInicioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonInicioActionPerformed
        logica.generarColoresRandom();
        textFieldUsuario.setEnabled(true); 
        botonComparar.setEnabled(true);
        if(logica.getIntentos() == 0  || logica.getAciertoTotal() == 4) {
            logica.resetJuego();
            labelPuntajeParcial.setText("Puntaje parcial: " + logica.getAciertoParcial());
            labelPuntajeTotal.setText("Puntaje Total: " + logica.getAciertoTotal());
            labelIntentos.setText("Intentos: " + logica.getIntentos());
        }
    }//GEN-LAST:event_botonInicioActionPerformed

