    private void botonCompararActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonCompararActionPerformed
        logica.compararArrays();
        
        inputUsuario = textFieldUsuario.getText();
        logica.setInputUsuario(inputUsuario);
        
        labelPuntajeParcial.setText("Puntaje parcial: " + logica.getAciertoParcial());
        labelPuntajeTotal.setText("Puntaje Total: " + logica.getAciertoTotal());
        labelIntentos.setText("Intentos: " + logica.getIntentos());      
        
        if(logica.getIntentos() > 0) {
            if(logica.getAciertoTotal() !=4) {
                labelResultado.setText("Ha fallado!");
                logica.resetPuntajes();
            }
            if(logica.getAciertoTotal() == 4) {        
                labelResultado.setText("Ha Ganado!!");
                botonComparar.setEnabled(false);
                logica.resetJuego();             
            }
        }
        else if(logica.getIntentos() == 0) {        
            labelResultado.setText("Game Over!!");
            botonComparar.setEnabled(false);
            textFieldUsuario.setEnabled(false); 
            logica.resetJuego();
        }
        else {

            logica.compararArrays();        
        }
    }//GEN-LAST:event_botonCompararActionPerformed
    
